import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import CircularProgress from '@mui/material/CircularProgress';
import Alert from '@mui/material/Alert';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Divider from '@mui/material/Divider';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import SummaryList from '../components/test/SummaryList';
import CodeViewer from '../components/test/CodeViewer';
import axios from 'axios';

const TestGenerationPage = () => {
  const navigate = useNavigate();
  const [files, setFiles] = useState([]);
  const [repoDetails, setRepoDetails] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState(0);
  const [fileSummaries, setFileSummaries] = useState([]);
  const [selectedSummary, setSelectedSummary] = useState(null);
  const [generatedCode, setGeneratedCode] = useState('');
  const [generatedFilePath, setGeneratedFilePath] = useState('');
  const [codeLoading, setCodeLoading] = useState(false);
  const [prCreated, setPrCreated] = useState(false);
  const [prUrl, setPrUrl] = useState('');

  useEffect(() => {
    // Retrieve file contents from session storage
    const storedFiles = sessionStorage.getItem('selectedFiles');
    const storedRepoDetails = sessionStorage.getItem('repoDetails');

    if (!storedFiles || !storedRepoDetails) {
      navigate('/repository');
      return;
    }

    try {
      const parsedFiles = JSON.parse(storedFiles);
      const parsedRepoDetails = JSON.parse(storedRepoDetails);

      setFiles(parsedFiles);
      setRepoDetails(parsedRepoDetails);

      // Generate summaries automatically
      generateSummaries(parsedFiles);
    } catch (err) {
      setError('Failed to load file data. Please try again.');
      console.error(err);
    }
  }, [navigate]);

  const generateSummaries = async (fileData) => {
    setLoading(true);
    setError('');

    try {
      const response = await axios.post('/api/ai/summarize', {
        files: fileData
      });

      setFileSummaries(response.data.results);
      setLoading(false);
    } catch (err) {
      setLoading(false);
      setError(
        err.response?.data?.message ||
        'Failed to generate test case summaries. Please try again.'
      );
    }
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const handleSummarySelect = (summary, filePath, fileContent) => {
    setSelectedSummary({
      ...summary,
      filePath,
      fileContent
    });
    setGeneratedCode('');
    setGeneratedFilePath('');
  };

  const handleGenerateCode = async () => {
  if (!selectedSummary) return;

  setCodeLoading(true);
  setError('');

  try {
    const response = await axios.post('/api/ai/generate-code', {
      summary: selectedSummary.summary || selectedSummary.description,
      language: selectedSummary.language || 'javascript',
      filePath: selectedSummary.filePath,
      fileContent: selectedSummary.fileContent
    });

    setGeneratedCode(response.data.testCode);
    setGeneratedFilePath(response.data.testFilePath);
    setCodeLoading(false);

    // Switch to code tab
    setActiveTab(1);
  } catch (err) {
    setCodeLoading(false);
    setError(
      err.response?.data?.message ||
      'Failed to generate test code. Please try again.'
    );
  }
};


  const handleCreatePR = async () => {
    if (!generatedCode || !generatedFilePath) return;

    setCodeLoading(true);
    setError('');

    try {
      const response = await axios.post('/api/github/create-pr', {
        owner: repoDetails.owner,
        repo: repoDetails.repo,
        token: repoDetails.token,
        files: [
          {
            path: generatedFilePath,
            content: generatedCode
          }
        ]
      });

      setPrCreated(true);
      setPrUrl(response.data.pullRequest.url);
      setCodeLoading(false);
    } catch (err) {
      setCodeLoading(false);
      setError(
        err.response?.data?.message ||
        'Failed to create pull request. Please check your GitHub token permissions.'
      );
    }
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
          <Button
            startIcon={<ArrowBackIcon />}
            onClick={() => navigate('/repository')}
            sx={{ mr: 2 }}
          >
            Back
          </Button>
          <Typography variant="h4" component="h1">
            Test Case Generator
          </Typography>
        </Box>

        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
            <CircularProgress />
            <Typography variant="body1" sx={{ ml: 2 }}>
              Analyzing code and generating test case summaries...
            </Typography>
          </Box>
        ) : (
          <Box sx={{ width: '100%' }}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
              <Tabs value={activeTab} onChange={handleTabChange}>
                <Tab label="Test Case Summaries" />
                <Tab 
                  label="Generated Test Code" 
                  disabled={!generatedCode} 
                />
              </Tabs>
            </Box>

            {/* Test Case Summaries Tab */}
            {activeTab === 0 && (
              <Box sx={{ mt: 3, display: 'flex' }}>
                <Paper 
                  elevation={2} 
                  sx={{ 
                    p: 2, 
                    width: '40%', 
                    mr: 2,
                    maxHeight: '70vh',
                    overflow: 'auto'
                  }}
                >
                  <Typography variant="h6" gutterBottom>
                    Files & Test Summaries
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Select a test case summary to generate code
                  </Typography>

                  {fileSummaries.length > 0 ? (
                    <SummaryList 
                      fileSummaries={fileSummaries} 
                      files={files}
                      onSelectSummary={handleSummarySelect}
                      selectedSummary={selectedSummary}
                    />
                  ) : (
                    <Typography variant="body2" color="text.secondary">
                      No test case summaries available
                    </Typography>
                  )}
                </Paper>

                <Paper 
                  elevation={2} 
                  sx={{ 
                    p: 2, 
                    width: '60%',
                    maxHeight: '70vh',
                    overflow: 'auto'
                  }}
                >
                  {selectedSummary ? (
                    <>
                      <Typography variant="h6" gutterBottom>
                        Selected Test Case
                      </Typography>

                      <Box sx={{ mb: 3 }}>
                        <Typography variant="subtitle1">
                          File: {selectedSummary.filePath}
                        </Typography>
                        <Typography variant="subtitle1">
                          Function: {selectedSummary.functionName}
                        </Typography>
                        <Typography variant="subtitle1">
                          Description: {selectedSummary.description}
                        </Typography>
                      </Box>

                      <Button
                        variant="contained"
                        onClick={handleGenerateCode}
                        disabled={codeLoading}
                        startIcon={codeLoading ? <CircularProgress size={20} /> : null}
                      >
                        {codeLoading ? 'Generating...' : 'Generate Test Code'}
                      </Button>
                    </>
                  ) : (
                    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100%' }}>
                      <Typography variant="body1" color="text.secondary">
                        Select a test case summary from the left panel
                      </Typography>
                    </Box>
                  )}
                </Paper>
              </Box>
            )}

            {/* Generated Test Code Tab */}
            {activeTab === 1 && generatedCode && (
              <Box sx={{ mt: 3 }}>
                <Paper elevation={2} sx={{ p: 2 }}>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                    <Typography variant="h6">
                      Generated Test Code
                    </Typography>
                    <Box>
                      {!prCreated ? (
                        <Button
                          variant="contained"
                          color="primary"
                          onClick={handleCreatePR}
                          disabled={codeLoading || !repoDetails.token}
                          startIcon={codeLoading ? <CircularProgress size={20} /> : null}
                          sx={{ mr: 1 }}
                        >
                          {codeLoading ? 'Creating PR...' : 'Create Pull Request'}
                        </Button>
                      ) : (
                        <Button
                          variant="outlined"
                          color="primary"
                          href={prUrl}
                          target="_blank"
                          rel="noopener noreferrer"
                        >
                          View Pull Request
                        </Button>
                      )}
                    </Box>
                  </Box>

                  <Typography variant="subtitle2" sx={{ mb: 1 }}>
                    Test File: {generatedFilePath}
                  </Typography>

                  <Divider sx={{ mb: 2 }} />

                  <CodeViewer 
                    code={generatedCode} 
                    language={selectedSummary?.language || 'javascript'} 
                  />
                </Paper>
              </Box>
            )}
          </Box>
        )}
      </Box>
    </Container>
  );
};

export default TestGenerationPage;
