import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import CircularProgress from '@mui/material/CircularProgress';
import Alert from '@mui/material/Alert';
import GitHubIcon from '@mui/icons-material/GitHub';
import FileList from '../components/github/FileList';
import axios from 'axios';

const RepositoryPage = () => {
  const navigate = useNavigate();
  const [repoDetails, setRepoDetails] = useState({
    owner: '',
    repo: '',
    token: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [files, setFiles] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [repoConnected, setRepoConnected] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setRepoDetails((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleConnectRepo = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await axios.get('/api/github/files', {
        params: {
          owner: repoDetails.owner,
          repo: repoDetails.repo,
          token: repoDetails.token || undefined,
        },
      });
      setFiles(response.data.files);
      setRepoConnected(true);
      setLoading(false);
    } catch (err) {
      setLoading(false);
      setError(
        err.response?.data?.message ||
          'Failed to connect to repository. Please check your details.'
      );
    }
  };

  const handleFileSelection = (filePath, isSelected) => {
    if (isSelected) {
      setSelectedFiles((prev) => [...prev, filePath]);
    } else {
      setSelectedFiles((prev) => prev.filter((path) => path !== filePath));
    }
  };

  const handleGenerateTests = async () => {
    if (selectedFiles.length === 0) {
      setError('Please select at least one file');
      return;
    }

    setLoading(true);
    try {
      // Fetch content of selected files
      const response = await axios.post('/api/github/content', {
        owner: repoDetails.owner,
        repo: repoDetails.repo,
        token: repoDetails.token || undefined,
        files: selectedFiles,
      });

      // Store the file contents in session storage to pass to the next page
      sessionStorage.setItem('selectedFiles', JSON.stringify(response.data.files));
      sessionStorage.setItem('repoDetails', JSON.stringify(repoDetails));

      // Navigate to test generation page
      navigate('/generate');
    } catch (err) {
      setLoading(false);
      setError(
        err.response?.data?.message ||
          'Failed to fetch file contents. Please try again.'
      );
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Connect to GitHub Repository
        </Typography>

        <Paper elevation={2} sx={{ p: 3, mb: 4 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
            <GitHubIcon color="action" sx={{ mr: 2 }} />
            <Typography variant="h6">Repository Details</Typography>
          </Box>

          <TextField
            label="Repository Owner"
            name="owner"
            value={repoDetails.owner}
            onChange={handleInputChange}
            fullWidth
            margin="normal"
            placeholder="e.g., facebook"
            disabled={loading || repoConnected}
          />

          <TextField
            label="Repository Name"
            name="repo"
            value={repoDetails.repo}
            onChange={handleInputChange}
            fullWidth
            margin="normal"
            placeholder="e.g., react"
            disabled={loading || repoConnected}
          />

          <TextField
            label="GitHub Token (optional for public repos)"
            name="token"
            value={repoDetails.token}
            onChange={handleInputChange}
            fullWidth
            margin="normal"
            type="password"
            placeholder="Your GitHub personal access token"
            helperText="For private repositories, you'll need a token with 'repo' scope"
            disabled={loading || repoConnected}
          />

          {error && (
            <Alert severity="error" sx={{ mt: 2 }}>
              {error}
            </Alert>
          )}

          <Box sx={{ mt: 2, display: 'flex', justifyContent: 'space-between' }}>
            {!repoConnected ? (
              <Button
                variant="contained"
                onClick={handleConnectRepo}
                disabled={loading || !repoDetails.owner || !repoDetails.repo}
                startIcon={loading ? <CircularProgress size={20} /> : null}
              >
                {loading ? 'Connecting...' : 'Connect Repository'}
              </Button>
            ) : (
              <Button
                variant="outlined"
                onClick={() => {
                  setRepoConnected(false);
                  setFiles([]);
                  setSelectedFiles([]);
                }}
              >
                Change Repository
              </Button>
            )}
          </Box>
        </Paper>

        {repoConnected && files.length > 0 && (
          <Paper elevation={2} sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Select Files for Test Generation
            </Typography>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
              Select the files you want to generate test cases for. Only code files are shown.
            </Typography>

            <FileList
              files={files}
              selectedFiles={selectedFiles}
              onFileSelect={handleFileSelection}
            />

            <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between' }}>
              <Typography variant="body2">
                {selectedFiles.length} file(s) selected
              </Typography>
              <Button
                variant="contained"
                color="primary"
                onClick={handleGenerateTests}
                disabled={selectedFiles.length === 0 || loading}
                startIcon={loading ? <CircularProgress size={20} /> : null}
              >
                {loading ? 'Processing...' : 'Generate Test Cases'}
              </Button>
            </Box>
          </Paper>
        )}

        {repoConnected && files.length === 0 && (
          <Alert severity="info">
            No code files found in this repository. Please try another repository.
          </Alert>
        )}
      </Box>
    </Container>
  );
};

export default RepositoryPage;
